$(function(){var r=$("section#employer-form form");r.on("ajax:success",function(){$("section#connect-form").html("<h4 class='center-align'>Thanks! Our Career Services team will be in touch shortly.<br><span class='emoji'>&#x1F64C;<span></h4>")}),r.on("ajax:error",function(r,e){var n=e.responseJSON.errors,o=$("#employer-errors"),s=o.find(".errors");s.empty(),n.forEach(function(r){s.append("<p>"+r+"</p>")}),o.slideDown()})});